mapper.select:
SELECT t.`postUUID` AS postUUID , t.`postPageviews` AS postPageviews, COUNT(*) AS commentNum, t.`postTitle` AS postTitle, u.`userName` AS userName, t.`postTime` AS postTime, MAX(cm.`cmTime`) AS cmTime FROM pb_comment cm RIGHT JOIN pb_post t ON cm.`postUUID`=t.`postUUID` LEFT JOIN pb_user u ON u.`userUUID`=t.`userUUID`
where t.`postTitle` LIKE CONCAT('%',#{postTitle},'%')
AND t.`userUUID`=#{userUUID}
where t.`userUUID`=#{userUUID}
GROUP BY t.`postUUID` limit #{pageIndex},#{everyPageDataCount}

	SELECT COUNT(*) FROM
